from context import LinSolve
"""
Test to invert list of bitstreams
"""
bitstreams = [
    {'bitstream': '01101'},
    {'bitstream': '0111001011100101',
    'degree': 3},
    {'bitstream': '10111100010011010111100010011010111100010',
    'degree': 4},
    {'bitstream': '11011000111110011010010000101011101100011',
     'degree': 5
    },
    {'bitstream': '11101100110101011111100000100001100010100111101000111001001011011101100110101011111100000100001100010',
     'degree': 6},
    {'bitstream': '0111011100110010101011111110000001000001100001010001111001000101100111010100111110100001110001001001101101011011110110001101001011101110011001010101111',
    'degree': 7},
    {'bitstream': '',
    'degree': 8}
]
if __name__ == '__main__':
    for capture in bitstreams:
        try:
            solver = LinSolve(**capture)
            solver.solve()
            print(f"""
            Degree: {solver.degree}\tTaps: {solver.tap_positions}
            """)
        except:
            pass 