from context import Analyser, Validator

BITSTREAM: str = ''
DEGREE: int = 7

analyser = Analyser(degree=DEGREE, stream=BITSTREAM)
analyser.lin_solve()

TAPS = analyser.tap_positions

validator = Validator(stream=BITSTREAM, degree=DEGREE, tap_positions=TAPS)
validator.validate()

def main():
    print(f"""
    Hamming length: {validator.hamming_length}\n 
    Accuracy: {100 * validator.accuracy:.2f} %
    """)

if __name__ == '__main__':
    main()
